const generateAndCopyButton=document.getElementById("generate-and-copy-button"),password=document.getElementById("password"),length=document.getElementById("length"),includeLowerCase=document.getElementById("includeLowerCase"),includeUpperCase=document.getElementById("includeUpperCase"),includeNumbers=document.getElementById("includeNumbers"),includeSymbols=document.getElementById("includeSymbols"),generatePassword=(e,n,t,d,c)=>{let o="",r="";if(o+=n?"abcdefghijklmnopqrstuvwxtz":"",o+=t?"ABCDEFGHIJKLMNOPQRSTUVWXYZ":"",o+=d?"0123456789":"",o+=c?"!@#$%^&*()_+?:{}[]":"",e<=0)return"Password length must be at least 1";if(0===o.length)return"At least 1 character set needs to be selected";for(let n=0;n<e;n++){r+=o[Math.round(Math.random()*(o.length-1))]}return r},generate=e=>[...Array(e)].map((()=>Math.random().toString(36)[2])).join("");function copy(){const e=parseInt(length.value),n=includeLowerCase.checked,t=includeUpperCase.checked,d=includeNumbers.checked,c=includeSymbols.checked;password.innerHTML=generatePassword(e,n,t,d,c);let o=document.createElement("input");document.body.appendChild(o),o.value=password.innerHTML,o.select(),document.execCommand("copy",!1),o.remove(),length.value=Math.trunc(length.value)}window.addEventListener("keypress",(function(e){"Enter"===e.key&&copy()})),generateAndCopyButton.addEventListener("click",(function(){copy()}));
